css:
.atomic-emacs .search {
  padding: 3px;
}
.atomic-emacs .search .row.inputs {
  display: flex;
  flex-flow: row nowrap;
  align-items: center;
}
.atomic-emacs .search .row.error {
  color: red;
}
.atomic-emacs .search .row.error.hidden {
  display: none;
}
.atomic-emacs .search label {
  flex: 0 0 auto;
  margin: 0.5em;
  min-width: 4em;
  text-align: center;
}
.atomic-emacs .search atom-text-editor {
  flex: 1 1;
  width: 100%;
}
.atomic-emacs .search button > svg.icon {
  width: 20px;
  height: 16px;
  vertical-align: middle;
  fill: currentColor;
  pointer-events: none;
}
.atomic-emacs .search-wrap-icon {
  position: absolute;
  top: 50% !important;
  left: 50% !important;
  right: initial !important;
  bottom: initial !important;
  margin-top: -60px;
  margin-left: -60px;
  opacity: 0;
  transition: opacity 0.5s;
}
.atomic-emacs .search-wrap-icon:before {
  font-size: 80px;
  line-height: 120px;
  width: 120px;
  height: 120px;
  color: #abb2bf;
  opacity: .5;
}
.atomic-emacs .search-wrap-icon.visible {
  opacity: 1;
}
atom-text-editor .atomic-emacs-search-result .region {
  background-color: transparent;
  border-radius: 3px;
  border: 1px solid rgba(82, 139, 255, 0.24);
  box-sizing: border-box;
  z-index: 0;
}
atom-text-editor .atomic-emacs-current-result .region {
  background-color: white;
  border-radius: 3px;
  border: 1px solid #528bff;
  box-sizing: border-box;
  z-index: 0;
}
atom-text-editor .atomic-emacs-search-result,
atom-text-editor .atomic-emacs-current-result {
  display: none;
}
atom-workspace.atomic-emacs-search-visible atom-text-editor .atomic-emacs-search-result,
atom-workspace.atomic-emacs-search-visible atom-text-editor .atomic-emacs-current-result {
  display: block;
}
